INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `id_danhmuc`) VALUES
(8, 'LAROSEbou001      ', '001       ', '69   ', 100, '1665593574_bou1.png', '<p>B&oacute; hoa hồng m&agrave;u v&agrave;ng tượng trưng cho t&igrave;nh bạn ch&acirc;n th&agrave;nh v&agrave; ấm &aacute;p. Gửi tặng một b&oacute; hoa hồng v&agrave;ng cho những người bạn th&acirc;n sẽ gi&uacute;p bạn thể h', '<p>B&oacute; hoa hồng m&agrave;u v&agrave;ng tượng trưng cho t&igrave;nh bạn ch&acirc;n th&agrave;nh v&agrave; ấm &aacute;p. Gửi tặng một b&oacute; hoa hồng v&agrave;ng cho những người bạn th&acirc;n sẽ gi&uacute;p bạn thể hiện t&igrave;nh cảm, sự quan t&acirc;m v&agrave; gợi nhắc lại những kỷ niệm t&igrave;nh bạn giữa 2 người.</p>\r\n', 1),
(9, 'LAROSEbou002 ', '002 ', '37 ', 99, '1665593655_bou2.png', '<p>B&oacute; hoa hồng m&agrave;u xanh dương l&agrave; biểu tượng của những điều kỳ diệu trong cuộc sống v&agrave; những nỗ lực biến những điều kh&ocirc;ng thể th&agrave;nh c&oacute; thể.</p>\r\n', '<p>B&oacute; hoa hồng m&agrave;u xanh dương kh&ocirc;ng c&oacute; thật trong tự nhi&ecirc;n. Những b&ocirc;ng hồng m&agrave;u xanh dương được tạo n&ecirc;n bằng c&aacute;ch nhuộm m&agrave;u xanh dương cho hoa hồng trắng. Hoa hồng m&agrave;u xanh dương l&agrave; biểu tượng của những điều kỳ diệu trong cuộc sống v&agrave; những nỗ lực biến những điều kh&ocirc;ng thể th&agrave;nh c&oacute; thể.</p>\r\n', 1),
(10, 'LAROSEbou003 ', '003 ', '33 ', 100, '1665593729_bou3.png', '<p>B&oacute; hoa hồng m&agrave;u hồng đậm tượng trưng cho t&igrave;nh y&ecirc;u mới chớm nở, n&oacute; ph&ugrave; hợp để d&agrave;nh tặng trong những lần gặp mặt đầu ti&ecirc;n hoặc cho người bạn g&aacute;i mới ', '<p>B&oacute; hoa hồng m&agrave;u hồng đậm tượng trưng cho t&igrave;nh y&ecirc;u mới chớm nở, n&oacute; ph&ugrave; hợp để d&agrave;nh tặng trong những lần gặp mặt đầu ti&ecirc;n hoặc cho người bạn g&aacute;i mới quen. Trong khi đ&oacute;, hoa hồng m&agrave;u hồng nhạt tượng trưng cho t&igrave;nh mẫu tử, sự biết ơn v&agrave; t&ocirc;n trọng. V&igrave; vậy, hoa hồng hồng nhạt cũng l&agrave; m&oacute;n qu&agrave; tặng mẹ ho&agrave;n hảo trong ng&agrave;y lễ của mẹ.</p>\r\n', 1),
(11, 'LAROSEbou004   ', '004   ', '100   ', 100, '1665593832_bou4.png', '<p style=\"text-align:justify\">B&oacute; hoa hồng 7 m&agrave;u c&ograve;n được gọi l&agrave; hoa hồng hạnh ph&uacute;c hay hoa hồng 7 m&agrave;u. Những b&ocirc;ng hoa độc đ&aacute;o n&agrave;y đ&aacute;ng nhớ kh&ocirc;ng chỉ v&igra', '<p style=\"text-align:justify\">B&oacute; hoa hồng 7 m&agrave;u c&ograve;n được gọi l&agrave; hoa hồng hạnh ph&uacute;c hay hoa hồng 7 m&agrave;u. Những b&ocirc;ng hoa độc đ&aacute;o n&agrave;y đ&aacute;ng nhớ kh&ocirc;ng chỉ v&igrave; m&agrave;u sắc m&agrave; c&ograve;n v&igrave; những &yacute; nghĩa đặc biệt của n&oacute;. Với những c&aacute;nh hoa rực rỡ, nhiều m&agrave;u, hoa hồng cầu vồng c&oacute; nghĩa l&agrave; hạnh ph&uacute;c v&agrave; niềm vui.</p>\r\n', 1),
(12, 'LAROSEbou005 ', '005 ', '41 ', 100, '1665593907_bou5.png', '<p>B&oacute; hoa hồng m&agrave;u cam l&agrave; sự pha trộn giữa hồng đỏ v&agrave; hồng v&agrave;ng. V&igrave; thế, hoa hồng m&agrave;u cam vừa c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh ', '<p>B&oacute; hoa hồng m&agrave;u cam l&agrave; sự pha trộn giữa hồng đỏ v&agrave; hồng v&agrave;ng. V&igrave; thế, hoa hồng m&agrave;u cam vừa c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh y&ecirc;u, sự đam m&ecirc;. Đồng thời cũng c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh bạn hoặc t&igrave;nh cảm giữa c&aacute;c người th&acirc;n trong gia đ&igrave;nh.</p>\r\n', 1),
(13, 'LaRoseBou006  ', '006  ', '54  ', 100, '1665593960_bou7.png', '<p style=\"text-align:justify\">B&oacute; hoa hồng đỏ gi&uacute;p bạn thể hiện mức độ cao nhất trong t&igrave;nh y&ecirc;u, đ&oacute; l&agrave; t&igrave;nh y&ecirc;u nồng ch&aacute;y v&agrave; s&acirc;u đậm. &Yacute; nghĩa thứ 2 c', '<p>B&oacute; hoa hồng đỏ gi&uacute;p bạn thể hiện mức độ cao nhất trong t&igrave;nh y&ecirc;u, đ&oacute; l&agrave; t&igrave;nh y&ecirc;u nồng ch&aacute;y v&agrave; s&acirc;u đậm. &Yacute; nghĩa thứ 2 của hoa hồng đỏ l&agrave; tượng trưng cho sự đam m&ecirc;. B&ecirc;n cạnh đ&oacute;, hoa hồng sọc đỏ trắng sẽ gi&uacute;p bạn gửi đi th&ocirc;ng điệp &ldquo;l&agrave;m vợ anh nh&eacute;&rdquo;. Hoa hồng đỏ ph&ugrave; hợp để d&agrave;nh tặng cho vợ, người y&ecirc;u trong ng&agrave;y sinh nhật hoặc ng&agrave;y lễ t&igrave;nh nh&acirc;n. Hoa hồng đỏ sọc trắng được d&ugrave;ng để cầu h&ocirc;n v&agrave; trong c&aacute;c buổi lễ đ&iacute;nh h&ocirc;n.</p>\r\n', 1),
(14, 'LAROSEbas001  ', '010  ', '60  ', 100, '1666772750_bas1.jpg', '<p>Lẵng hoa hồng m&agrave;u cam l&agrave; sự pha trộn giữa hồng đỏ v&agrave; hồng v&agrave;ng. V&igrave; thế, hoa hồng m&agrave;u cam vừa c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh y&e', '<p>Lẵng hoa hồng m&agrave;u cam l&agrave; sự pha trộn giữa hồng đỏ v&agrave; hồng v&agrave;ng. V&igrave; thế, hoa hồng m&agrave;u cam vừa c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh y&ecirc;u, sự đam m&ecirc;. Đồng thời cũng c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh bạn hoặc t&igrave;nh cảm giữa c&aacute;c người th&acirc;n trong gia đ&igrave;nh.</p>\r\n', 3),
(15, 'LAROSEbas002  ', '011  ', '61  ', 100, '1666772763_bas8.jpg', '<p>Lẵng hoa hồng m&agrave;u xanh dương l&agrave; sự pha trộn giữa hồng t&iacute;m v&agrave; hồng hồng. V&igrave; thế, hoa hồng m&agrave;u xanh dương vừa c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng c', '<p>Lẵng hoa hồng m&agrave;u xanh dương l&agrave; sự pha trộn giữa hồng t&iacute;m v&agrave; hồng hồng. V&igrave; thế, hoa hồng m&agrave;u xanh dương vừa c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh y&ecirc;u, Đồng thời cũng c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh bạn hoặc t&igrave;nh cảm giữa c&aacute;c người th&acirc;n trong gia đ&igrave;nh.</p>\r\n', 3),
(16, 'LAROSEbas003  ', '013  ', '41  ', 99, '1666772773_bas6.jpg', '<p>Lẵng hoa hồng m&agrave;u hồng l&agrave; sự pha trộn giữa hồng đỏ v&agrave; hồng v&agrave;ng. V&igrave; thế, hoa hồng m&agrave;u cam vừa c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh ', '<p>Lẵng hoa hồng m&agrave;u hồng l&agrave; sự pha trộn giữa hồng đỏ v&agrave; hồng v&agrave;ng. V&igrave; thế, hoa hồng m&agrave;u cam vừa c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh y&ecirc;u, sự đam m&ecirc;. Đồng thời cũng c&oacute; thể được d&ugrave;ng với &yacute; nghĩa tượng trưng cho t&igrave;nh bạn hoặc t&igrave;nh cảm giữa c&aacute;c người th&acirc;n trong gia đ&igrave;nh.</p>\r\n', 3),
(17, 'LAROSEbas004  ', '014  ', '70  ', 100, '1666772785_bas3.jpg', '<p>Lẵng hoa hồng m&agrave;u trắng l&agrave; giống hoa hồng tự nhi&ecirc;n. Hoa hồng xanh l&aacute; l&agrave; biểu tượng của sự th&agrave;nh c&ocirc;ng v&agrave; những cơ hội mới trong cuộc sống.</p>\r\n', '<p>Lẵng hoa hồng m&agrave;u trắng l&agrave; giống hoa hồng tự nhi&ecirc;n. Hoa hồng xanh l&aacute; l&agrave; biểu tượng của sự th&agrave;nh c&ocirc;ng v&agrave; những cơ hội mới trong cuộc sống.</p>\r\n', 3),
(18, 'LAROSEbox001 ', '021 ', '38 ', 100, '1665594282_box2.png', '<p>Hộp hoa hồng m&agrave;u hồng đậm tượng trưng cho t&igrave;nh y&ecirc;u mới chớm nở, n&oacute; ph&ugrave; hợp để d&agrave;nh tặng trong những lần gặp mặt đầu ti&ecirc;n hoặc cho người bạn g&aacute;i mới quen', '<p>Hộp hoa hồng m&agrave;u hồng đậm tượng trưng cho t&igrave;nh y&ecirc;u mới chớm nở, n&oacute; ph&ugrave; hợp để d&agrave;nh tặng trong những lần gặp mặt đầu ti&ecirc;n hoặc cho người bạn g&aacute;i mới quen.</p>\r\n', 2),
(19, 'LAROSEbox002  ', '022  ', '55  ', 100, '1668283781_box20.png', '<p>Hộp hoa hồng m&agrave;u t&iacute;m kh&ocirc;ng c&oacute; thật trong tự nhi&ecirc;n. Những b&ocirc;ng hồng m&agrave;u xanh dương được tạo n&ecirc;n bằng c&aacute;ch nhuộm m&agrave;u xanh dương cho hoa hồng trắng. Hoa hồng', '<p>Hộp hoa hồng m&agrave;u t&iacute;m kh&ocirc;ng c&oacute; thật trong tự nhi&ecirc;n. Những b&ocirc;ng hồng m&agrave;u xanh dương được tạo n&ecirc;n bằng c&aacute;ch nhuộm m&agrave;u xanh dương cho hoa hồng trắng. Hoa hồng m&agrave;u xanh dương l&agrave; biểu tượng của những điều kỳ diệu trong cuộc sống v&agrave; những nỗ lực biến những điều kh&ocirc;ng thể th&agrave;nh c&oacute; thể.</p>\r\n', 2),
(20, 'LAROSEbox003 ', '023 ', '67 ', 99, '1665594369_box13.png', '<p>Hộp hoa hồng 7 m&agrave;u c&ograve;n được gọi l&agrave; hoa hồng hạnh ph&uacute;c hay hoa hồng 7 m&agrave;u. Những b&ocirc;ng hoa độc đ&aacute;o n&agrave;y đ&aacute;ng nhớ kh&ocirc;ng chỉ v&igrave; m&agrave;u sắc m&agrave; ', '<p>Hộp hoa hồng 7 m&agrave;u c&ograve;n được gọi l&agrave; hoa hồng hạnh ph&uacute;c hay hoa hồng 7 m&agrave;u. Những b&ocirc;ng hoa độc đ&aacute;o n&agrave;y đ&aacute;ng nhớ kh&ocirc;ng chỉ v&igrave; m&agrave;u sắc m&agrave; c&ograve;n v&igrave; những &yacute; nghĩa đặc biệt của n&oacute;.</p>\r\n', 2),
(21, 'LAROSEbox004  ', '024  ', '41  ', 100, '1665594430_box12.png', '<p>Hộp hoa hồng m&agrave;u đỏ ph&ugrave; hợp để d&agrave;nh tặng cho vợ, người y&ecirc;u trong ng&agrave;y sinh nhật hoặc ng&agrave;y lễ t&igrave;nh nh&acirc;n. Hoa hồng đỏ sọc trắng được d&ugrave;ng để cầu h&oci', '<p>Hộp hoa hồng m&agrave;u đỏ ph&ugrave; hợp để d&agrave;nh tặng cho vợ, người y&ecirc;u trong ng&agrave;y sinh nhật hoặc ng&agrave;y lễ t&igrave;nh nh&acirc;n. Hoa hồng đỏ sọc trắng được d&ugrave;ng để cầu h&ocirc;n v&agrave; trong c&aacute;c buổi lễ đ&iacute;nh h&ocirc;n.</p>\r\n', 2),
(22, 'LAROSEbox005 ', '025 ', '49 ', 99, '1665594472_box11.png', '<p>Hộp hoa hồng m&agrave;u xanh l&aacute; l&agrave; giống hoa hồng tự nhi&ecirc;n. Hoa hồng xanh l&aacute; l&agrave; biểu tượng của sự th&agrave;nh c&ocirc;ng v&agrave; những cơ hội mới trong cuộc sống.</p>\r\n', '<p>Hộp hoa hồng m&agrave;u xanh l&aacute; l&agrave; giống hoa hồng tự nhi&ecirc;n. Hoa hồng xanh l&aacute; l&agrave; biểu tượng của sự th&agrave;nh c&ocirc;ng v&agrave; những cơ hội mới trong cuộc sống.</p>\r\n', 2),
(23, 'LAROSEbox006 ', '026 ', '37 ', 100, '1665594514_box18.png', '<p>Hộp hoa hồng m&agrave;u hồng l&agrave; biểu tượng của sự l&atilde;ng mạn v&agrave; t&igrave;nh y&ecirc;u thủy chung. Hoa hồng t&iacute;m l&agrave; lựa chọn ho&agrave;n hảo để l&agrave;m hoa tặng sinh nhật người y&ec', '<p>Hộp hoa hồng m&agrave;u hồng l&agrave; biểu tượng của sự l&atilde;ng mạn v&agrave; t&igrave;nh y&ecirc;u thủy chung. Hoa hồng t&iacute;m l&agrave; lựa chọn ho&agrave;n hảo để l&agrave;m hoa tặng sinh nhật người y&ecirc;u. B&ecirc;n cạnh đ&oacute;, hoa hồng t&iacute;m cũng thường được chọn để l&agrave;m hoa trang tr&iacute; trong c&aacute;c lễ cưới hay buổi tiệc sang trọng.</p>\r\n', 2),
(24, 'LAROSEbou007 ', '007 ', '60 ', 100, '1665594660_bou12.png', '<p>C&oacute; nhiều người n&oacute;i rằng, b&oacute; hoa hồng đen l&agrave; biểu tượng của t&igrave;nh y&ecirc;u vĩnh cửu, sự b&iacute; ẩn. Tuy nhi&ecirc;n, trong nền văn h&oacute;a của một số quốc gia th&igrave; hoa hồ', '<p>C&oacute; nhiều người n&oacute;i rằng, b&oacute; hoa hồng đen l&agrave; biểu tượng của t&igrave;nh y&ecirc;u vĩnh cửu, sự b&iacute; ẩn. Tuy nhi&ecirc;n, trong nền văn h&oacute;a của một số quốc gia th&igrave; hoa hồng đen lại tượng trưng cho sự đổ vỡ v&agrave; mất đi người y&ecirc;u. Hoa hồng đen cũng được d&ugrave;ng để thể hiện sự mệt mỏi hay nỗi buồn.</p>\r\n', 1),
(25, 'LAROSEbas005  ', '015  ', '49  ', 0, '1666772813_bas7.jpg', '<p>Lẵng hoa hồng m&agrave;u t&iacute;m l&agrave; biểu tượng của sự l&atilde;ng mạn v&agrave; t&igrave;nh y&ecirc;u thủy chung. Hoa hồng t&iacute;m l&agrave; lựa chọn ho&agrave;n hảo để l&agrave;m hoa tặng sinh nhật người', '<p>Lẵng hoa hồng m&agrave;u t&iacute;m l&agrave; biểu tượng của sự l&atilde;ng mạn v&agrave; t&igrave;nh y&ecirc;u thủy chung. Hoa hồng t&iacute;m l&agrave; lựa chọn ho&agrave;n hảo để l&agrave;m hoa tặng sinh nhật người y&ecirc;u. B&ecirc;n cạnh đ&oacute;, hoa hồng t&iacute;m cũng thường được chọn để l&agrave;m hoa trang tr&iacute; trong c&aacute;c lễ cưới hay buổi tiệc sang trọng.</p>\r\n', 3),
(26, 'LAROSEbou008 ', '008 ', '37 ', 0, '1665594761_bou13.png', '<p>B&oacute; hoa hồng m&agrave;u xanh dương kh&ocirc;ng c&oacute; thật trong tự nhi&ecirc;n. Những b&ocirc;ng hồng m&agrave;u xanh dương được tạo n&ecirc;n bằng c&aacute;ch nhuộm m&agrave;u xanh dương cho hoa hồng trắng. Hoa ', '<p>B&oacute; hoa hồng m&agrave;u xanh dương kh&ocirc;ng c&oacute; thật trong tự nhi&ecirc;n. Những b&ocirc;ng hồng m&agrave;u xanh dương được tạo n&ecirc;n bằng c&aacute;ch nhuộm m&agrave;u xanh dương cho hoa hồng trắng. Hoa hồng m&agrave;u xanh dương l&agrave; biểu tượng của những điều kỳ diệu trong cuộc sống v&agrave; những nỗ lực biến những điều kh&ocirc;ng thể th&agrave;nh c&oacute; thể.</p>\r\n', 1),
(27, 'LAROSEbou009 ', '009 ', '41 ', 100, '1665594815_bou8.png', '<p>B&oacute; hoa hồng m&agrave;u trắng l&agrave; biểu tượng của sự thuần khiết, trong s&aacute;ng. Tặng một b&ocirc;ng hoa hồng trắng cho người m&agrave; bạn y&ecirc;u thương sẽ gửi đi th&ocirc;ng điệp rằng &ldquo', '<p>B&oacute; hoa hồng m&agrave;u trắng l&agrave; biểu tượng của sự thuần khiết, trong s&aacute;ng. Tặng một b&ocirc;ng hoa hồng trắng cho người m&agrave; bạn y&ecirc;u thương sẽ gửi đi th&ocirc;ng điệp rằng &ldquo;Bạn rất tr&acirc;n trọng t&igrave;nh y&ecirc;u của 2 người v&agrave; mong muốn t&igrave;nh y&ecirc;u của 2 người sẽ k&eacute;o d&agrave;i m&atilde;i m&atilde;i&rdquo;.</p>\r\n', 1),
(28, 'LAROSEbox007    ', '027    ', '30    ', 100, '1668284565_box3.png', '<p style=\"text-align:justify\">Hộp hoa hồng m&agrave;u đỏ l&agrave; biểu tượng của sự l&atilde;ng mạn v&agrave; t&igrave;nh y&ecirc;u thủy chung. Hoa hồng t&iacute;m l&agrave; lựa chọn ho&agrave;n hảo để l&agrave;m hoa tặng', '<p style=\"text-align:justify\">Hộp hoa hồng m&agrave;u đỏ l&agrave; biểu tượng của sự l&atilde;ng mạn v&agrave; t&igrave;nh y&ecirc;u thủy chung. Hoa hồng t&iacute;m l&agrave; lựa chọn ho&agrave;n hảo để l&agrave;m hoa tặng sinh nhật người y&ecirc;u. B&ecirc;n cạnh đ&oacute;, hoa hồng t&iacute;m cũng thường được chọn để l&agrave;m hoa trang tr&iacute; trong c&aacute;c lễ cưới hay buổi tiệc sang trọng.</p>\r\n', 2),
(29, 'LAROSEbox008       ', '028       ', '41       ', 1001, '1668284585_box9.png', '<p style=\"text-align:justify\">Hộp hoa m&agrave;u xanh dương tượng trưng cho t&igrave;nh bạn ch&acirc;n th&agrave;nh v&agrave; ấm &aacute;p. Gửi tặng một b&oacute; hoa hồng v&agrave;ng cho những người bạn th&acirc;n sẽ...</p>\r\n', '<p style=\"text-align:justify\">Hộp hoa hồng m&agrave;u dương tượng trưng cho t&igrave;nh bạn ch&acirc;n th&agrave;nh v&agrave; ấm &aacute;p. Gửi tặng một b&oacute; hoa hồng v&agrave;ng cho những người bạn th&acirc;n sẽ gi&uacute;p bạn thể hiện t&igrave;nh cảm, sự quan t&acirc;m v&agrave; gợi nhắc lại những kỷ niệm t&igrave;nh bạn giữa 2 người.</p>\r\n', 2),
(31, 'LAROSEbou010  ', '010  ', '41  ', 100, '1666115900_bou15.png', '<p style=\"text-align:justify\">B&oacute; hoa hồng m&agrave;u xanh dương l&agrave; biểu tượng của những điều kỳ diệu trong cuộc sống v&agrave; những nỗ lực biến những điều kh&ocirc;ng thể th&agrave;nh c&oacute; thể.<', '<p style=\"text-align:justify\">B&oacute; hoa hồng m&agrave;u xanh dương l&agrave; biểu tượng của những điều kỳ diệu trong cuộc sống v&agrave; những nỗ lực biến những điều kh&ocirc;ng thể th&agrave;nh c&oacute; thể. Hoa hồng m&agrave;u xanh dương l&agrave; biểu tượng của những điều kỳ diệu trong cuộc sống v&agrave; những nỗ lực biến những điều kh&ocirc;ng thể th&agrave;nh c&oacute; thể</p>\r\n', 1),
(32, 'LAROSEbou011      ', '011      ', '60      ', 99, '1668283843_box21.png', '<p style=\"text-align:justify\">Hộp hoa hồng m&agrave;u cam l&agrave; giống hoa hồng tự nhi&ecirc;n. Hoa hồng xanh l&aacute; l&agrave; biểu tượng của sự th&agrave;nh c&ocirc;ng v&agrave; những cơ hội mới trong cuộc sống..</p>', '<p style=\"text-align:justify\">Hộp hoa hồng m&agrave;u cam l&agrave; giống hoa hồng tự nhi&ecirc;n. Hoa hồng xanh l&aacute; l&agrave; biểu tượng của sự th&agrave;nh c&ocirc;ng v&agrave; những cơ hội mới trong cuộc sống. Hoa hồng m&agrave;u xanh l&aacute; l&agrave; giống hoa hồng tự nhi&ecirc;n. Hoa hồng xanh l&aacute; l&agrave; biểu tượng của sự th&agrave;nh c&ocirc;ng v&agrave; những cơ hội mới trong cuộc sống.</p>\r\n', 2);

